mapper.select:
SELECT DISTINCT page
FROM member_log
WHERE profile_id = #{profileId}
  AND book_id = #{bookId}
  AND TRUNC(created_at) BETWEEN #{startDate} AND #{endDate}

SELECT DISTINCT rl.BOOK_ID
FROM member_log rl
         JOIN fairy_tale ft ON rl.BOOK_ID = ft.id
WHERE rl.profile_id = #{childProfileId}
  AND ft.category_id = #{categoryId}
  AND TRUNC(rl.created_at) BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(id)
FROM member_log
WHERE profile_id = #{profileId}
  AND log_type = 'CREATE'
  AND TRUNC(created_at) BETWEEN #{startDate} AND #{endDate}
