mapper.select:
SELECT id, nickname, profile_image AS profileImage, role, age, gender
FROM profile
WHERE member_id = #{memberId}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM profile
WHERE member_id = #{memberId}
AND id = #{profileId}

mapper.delete:
DELETE FROM profile
WHERE member_id = #{memberId}
AND id = #{profileId}

mapper.select:
SELECT id, nickname, profile_image AS profileImage, role, age, gender, password
FROM profile
WHERE id = #{profileId}
